import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function SchoolSelection() {
  const [formData, setFormData] = useState({
    name: '',
    contact: '',
    personalWebsite: '',
    currentEducation: {
      degree: '',
      program: '',
      institution: '',
      startDate: '',
      endDate: '',
    },
    additionalEducation: [],
    internships: [],
    researches: [],
    gpa: '',
    desiredMajor: '',
    preferredRegion: '',
  });

  const [schoolList, setSchoolList] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  // Load saved information from localStorage when the user clicks the load button
  const handleLoadSavedInfo = () => {
    const savedInfo = JSON.parse(localStorage.getItem('userInfo'));
    if (savedInfo) {
      setFormData({
        ...formData,
        name: savedInfo.name || '',
        contact: savedInfo.contact || '',
        personalWebsite: savedInfo.personalWebsite || '',
        currentEducation: savedInfo.currentEducation || { degree: '', program: '', institution: '', startDate: '', endDate: '' },
        additionalEducation: savedInfo.additionalEducation || [],
        internships: savedInfo.internships || [],
        researches: savedInfo.researches || [],
      });
    } else {
      alert('No saved user information found.');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    try {
      const response = await fetch('http://localhost:5001/school_selection', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      const data = await response.json();
      setSchoolList(data.schools);
    } catch (error) {
      console.error('Error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const loaderStyle = {
    border: '8px solid #f3f3f3',
    borderTop: '8px solid #3498db',
    borderRadius: '50%',
    width: '50px',
    height: '50px',
    animation: 'spin 2s linear infinite',
    margin: '20px auto',
  };

  const keyframesStyle = `
    @keyframes spin {
      0% { transform: rotate(0deg); }
      100% { transform: rotate(360deg); }
    }
  `;

  return (
    <div className="container">
      <style>{keyframesStyle}</style> {/* Add the keyframes to the document */}
      <h2 className="mb-4 page-title">School Selection</h2>
      <button type="button" className="btn btn-secondary mb-4" onClick={handleLoadSavedInfo}>
        Load Saved Information
      </button>
      <form onSubmit={handleSubmit}>
        {/* Basic Information */}
        <div className="row">
          <div className="form-group col-md-4">
            <label>GPA</label>
            <input
              type="text"
              name="gpa"
              className="form-control"
              value={formData.gpa}
              onChange={handleChange}
            />
          </div>
          <div className="form-group col-md-4">
            <label>Desired Major</label>
            <input
              type="text"
              name="desiredMajor"
              className="form-control"
              value={formData.desiredMajor}
              onChange={handleChange}
            />
          </div>
          <div className="form-group col-md-4">
            <label>Preferred Region</label>
            <input
              type="text"
              name="preferredRegion"
              className="form-control"
              value={formData.preferredRegion}
              onChange={handleChange}
            />
          </div>
        </div>

        <div className='text-center mt-4'>
          <button type="submit" className="btn btn-primary mt-3">Generate School List</button>
        </div>
      </form>

      {isLoading && <div style={loaderStyle}></div>}

      {/* Display School List */}
      {schoolList.length > 0 && (
        <div className="mt-5">
          <h3>Suggested Schools:</h3>
          <table className="table">
            <thead>
              <tr>
                <th>School Name</th>
                <th>Ranking (Overall & Major)</th>
                <th>Program Name</th>
                <th>City</th>
                <th>Tuition Fee</th>
                <th>Link</th>
              </tr>
            </thead>
            <tbody>
              {schoolList.map((school, index) => (
                <tr key={index}>
                  <td>{school.name}</td>
                  <td>{school.ranking}</td>
                  <td>{school.program}</td>
                  <td>{school.city}</td>
                  <td>{school.tuition}</td>
                  <td><a href={school.link} target="_blank" rel="noopener noreferrer">View Program</a></td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default SchoolSelection;
