import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function SchoolSelectionPlan() {
  const [formData, setFormData] = useState({
    desiredProgram: '',
    targetRegion: '',
    educationRecords: [],
    internships: [],
    researches: [],
  });

  // Load saved information from localStorage
  useEffect(() => {
    const savedInfo = JSON.parse(localStorage.getItem('userInfo'));
    if (savedInfo) {
      setFormData({
        ...formData,
        educationRecords: savedInfo.additionalEducation.map(edu => ({
          ...edu,
          gpa: '', // Adding a GPA field
        })),
        internships: savedInfo.internships || [],
        researches: savedInfo.researches || [],
      });
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleEducationChange = (index, e) => {
    const { name, value } = e.target;
    const updatedEducation = formData.educationRecords.map((edu, i) =>
      i === index ? { ...edu, [name]: value } : edu
    );
    setFormData({ ...formData, educationRecords: updatedEducation });
  };

  const addEducationRecord = () => {
    setFormData({
      ...formData,
      educationRecords: [
        ...formData.educationRecords,
        { degree: '', program: '', institution: '', startDate: '', endDate: '', gpa: '' },
      ],
    });
  };

  const removeEducationRecord = (index) => {
    const updatedEducation = formData.educationRecords.filter((_, i) => i !== index);
    setFormData({ ...formData, educationRecords: updatedEducation });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Mock backend call to fetch school recommendations
    const recommendedSchools = [
      { name: 'Harvard University', rank: '2', programRank: '3', program: 'Data Science', city: 'Cambridge', tuition: '$50,000', link: 'https://www.harvard.edu' },
      { name: 'Stanford University', rank: '3', programRank: '1', program: 'Computer Science', city: 'Stanford', tuition: '$55,000', link: 'https://www.stanford.edu' },
      { name: 'University of Oxford', rank: '5', programRank: '2', program: 'Machine Learning', city: 'Oxford', tuition: '£45,000', link: 'https://www.ox.ac.uk' },
    ];

    // You can replace this mock with real logic to fetch data based on user background
    alert('Suggested Schools have been updated!');
    setSchoolList(recommendedSchools);
  };

  const [schoolList, setSchoolList] = useState([]);

  return (
    <div className="container mt-5">
      <h2 className="mb-4 page-title">School Selection Plan</h2>
      <form onSubmit={handleSubmit}>
        <div className="row">
          <div className="form-group col-md-6">
            <label>Desired Program</label>
            <input
              type="text"
              name="desiredProgram"
              className="form-control"
              value={formData.desiredProgram}
              onChange={handleChange}
            />
          </div>
          <div className="form-group col-md-6">
            <label>Target Region/Country</label>
            <input
              type="text"
              name="targetRegion"
              className="form-control"
              value={formData.targetRegion}
              onChange={handleChange}
            />
          </div>
        </div>

        {/* Education Records */}
        <h4>Education Records</h4>
        {formData.educationRecords.map((edu, index) => (
          <div className="row" key={index}>
            <div className="form-group col-md-2">
              <label>Degree</label>
              <input
                type="text"
                name="degree"
                className="form-control"
                value={edu.degree}
                onChange={(e) => handleEducationChange(index, e)}
              />
            </div>
            <div className="form-group col-md-2">
              <label>Program</label>
              <input
                type="text"
                name="program"
                className="form-control"
                value={edu.program}
                onChange={(e) => handleEducationChange(index, e)}
              />
            </div>
            <div className="form-group col-md-2">
              <label>Institution</label>
              <input
                type="text"
                name="institution"
                className="form-control"
                value={edu.institution}
                onChange={(e) => handleEducationChange(index, e)}
              />
            </div>
            <div className="form-group col-md-2">
              <label>GPA</label>
              <input
                type="text"
                name="gpa"
                className="form-control"
                value={edu.gpa}
                onChange={(e) => handleEducationChange(index, e)}
              />
            </div>
            <div className="form-group col-md-2">
              <label>Start Date</label>
              <input
                type="date"
                name="startDate"
                className="form-control"
                value={edu.startDate}
                onChange={(e) => handleEducationChange(index, e)}
              />
            </div>
            <div className="form-group col-md-2">
              <label>End Date</label>
              <input
                type="date"
                name="endDate"
                className="form-control"
                value={edu.endDate}
                onChange={(e) => handleEducationChange(index, e)}
              />
            </div>
            <div className="form-group col-md-1 d-flex align-items-end">
              <button type="button" className="btn btn-danger" onClick={() => removeEducationRecord(index)}>
                -
              </button>
            </div>
          </div>
        ))}
        <button type="button" className="btn btn-secondary mt-3 mb-4" onClick={addEducationRecord}>
          + Add Education Record
        </button>

        <div className="text-center">
          <button type="submit" className="btn btn-primary mt-3">Generate School Suggestions</button>
        </div>
      </form>

      {/* Display Suggested Schools */}
      {schoolList.length > 0 && (
        <div className="mt-5">
          <h3>Suggested Schools:</h3>
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>School Name</th>
                <th>Overall Rank & Program Rank</th>
                <th>Program</th>
                <th>City</th>
                <th>Tuition</th>
                <th>Link</th>
              </tr>
            </thead>
            <tbody>
              {schoolList.map((school, index) => (
                <tr key={index}>
                  <td>{school.name}</td>
                  <td>{school.rank} | {school.programRank}</td>
                  <td>{school.program}</td>
                  <td>{school.city}</td>
                  <td>{school.tuition}</td>
                  <td><a href={school.link} target="_blank" rel="noopener noreferrer">Visit</a></td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default SchoolSelectionPlan;
