import React, { useState } from 'react';

function PersonalStatementCreation() {
  const [formData, setFormData] = useState({
    current_degree: '',
    current_program: '',
    institution: '',
    degree: '',
    program: '',
    institution_applying: '',
    internship: '',
    research: '',
    other_requirements: '',
    word_count: '',
    additional_education: [{ degree: '', program: '', institution: '' }]
  });

  const [generatedPS, setGeneratedPS] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleEducationChange = (index, e) => {
    const { name, value } = e.target;
    const newEducation = formData.additional_education.map((edu, eduIndex) =>
      eduIndex === index ? { ...edu, [name]: value } : edu
    );
    setFormData({ ...formData, additional_education: newEducation });
  };

  const addEducation = () => {
    setFormData({
      ...formData,
      additional_education: [...formData.additional_education, { degree: '', program: '', institution: '' }]
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setGeneratedPS('');
    console.log("Submitting data:", formData);
    try {
      const response = await fetch('http://localhost:5001/ps_create', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      const data = await response.json();
      console.log("Response data:", data);
      setGeneratedPS(data.personal_statement);
    } catch (error) {
      console.error('Error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const loaderStyle = {
    border: '8px solid #f3f3f3',
    borderTop: '8px solid #3498db',
    borderRadius: '50%',
    width: '50px',
    height: '50px',
    animation: 'spin 2s linear infinite',
    margin: '20px auto',
  };

  const keyframesStyle = `
    @keyframes spin {
      0% { transform: rotate(0deg); }
      100% { transform: rotate(360deg); }
    }
  `;

  return (
    <div>
      <h2 className="mb-4">Create Personal Statement</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Current Degree</label>
          <input type="text" name="current_degree" className="form-control" value={formData.current_degree} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Current Program</label>
          <input type="text" name="current_program" className="form-control" value={formData.current_program} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Institution</label>
          <input type="text" name="institution" className="form-control" value={formData.institution} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Degree Applying For</label>
          <input type="text" name="degree" className="form-control" value={formData.degree} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Program Applying For</label>
          <input type="text" name="program" className="form-control" value={formData.program} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Institution Applying To</label>
          <input type="text" name="institution_applying" className="form-control" value={formData.institution_applying} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Internship Experience</label>
          <input type="text" name="internship" className="form-control" value={formData.internship} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Research Experience</label>
          <input type="text" name="research" className="form-control" value={formData.research} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Other Requirements</label>
          <input type="text" name="other_requirements" className="form-control" value={formData.other_requirements} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Word Count</label>
          <input type="text" name="word_count" className="form-control" value={formData.word_count} onChange={handleChange} />
        </div>
        {formData.additional_education.map((edu, index) => (
          <div key={index} className="form-group">
            <label>Additional Degree {index + 1}</label>
            <input type="text" name="degree" className="form-control" value={edu.degree} onChange={(e) => handleEducationChange(index, e)} />
            <label>Program</label>
            <input type="text" name="program" className="form-control" value={edu.program} onChange={(e) => handleEducationChange(index, e)} />
            <label>Institution</label>
            <input type="text" name="institution" className="form-control" value={edu.institution} onChange={(e) => handleEducationChange(index, e)} />
          </div>
        ))}
        <button type="button" className="btn btn-secondary" onClick={addEducation}>Add More Education</button>
        <button type="submit" className="btn btn-primary mt-3">Generate Personal Statement</button>
      </form>
      {isLoading && <div className="loader"></div>}
      {generatedPS && (
        <div className="mt-5">
          <h3>Generated Personal Statement:</h3>
          {/*<p className="border p-3">{generatedPS}</p>*/}
          <p dangerouslySetInnerHTML={{ __html: generatedPS.replace(/\n/g, '<br>') }} className="border p-3"></p>
        </div>
      )}
    </div>
  );
}

export default PersonalStatementCreation;
