import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function ResumeCreation() {
  const [resumeData, setResumeData] = useState({
    name: '',
    contact: '',
    personalWebsite: '',
    currentEducation: {
      degree: '',
      program: '',
      institution: '',
      startDate: '',
      endDate: '',
    },
    additionalEducation: [],
    internships: [],
    researches: [],
    skills: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setResumeData({
      ...resumeData,
      [name]: value
    });
  };

  const handleEducationChange = (index, e, type) => {
    const { name, value } = e.target;
    if (type === 'current') {
      setResumeData({
        ...resumeData,
        currentEducation: { ...resumeData.currentEducation, [name]: value }
      });
    } else {
      const updatedEducation = resumeData.additionalEducation.map((edu, i) =>
        i === index ? { ...edu, [name]: value } : edu
      );
      setResumeData({ ...resumeData, additionalEducation: updatedEducation });
    }
  };

  const addAdditionalEducation = () => {
    setResumeData({
      ...resumeData,
      additionalEducation: [...resumeData.additionalEducation, { degree: '', program: '', institution: '', startDate: '', endDate: '' }]
    });
  };

  const removeAdditionalEducation = (index) => {
    const updatedEducation = resumeData.additionalEducation.filter((_, i) => i !== index);
    setResumeData({ ...resumeData, additionalEducation: updatedEducation });
  };

  const handleInternshipChange = (index, e) => {
    const { name, value } = e.target;
    const updatedInternships = resumeData.internships.map((intern, i) =>
      i === index ? { ...intern, [name]: value } : intern
    );
    setResumeData({ ...resumeData, internships: updatedInternships });
  };

  const addInternship = () => {
    setResumeData({
      ...resumeData,
      internships: [...resumeData.internships, { company: '', role: '', location: '', startDate: '', endDate: '', description: '' }]
    });
  };

  const removeInternship = (index) => {
    const updatedInternships = resumeData.internships.filter((_, i) => i !== index);
    setResumeData({ ...resumeData, internships: updatedInternships });
  };

  const handleResearchChange = (index, e) => {
    const { name, value } = e.target;
    const updatedResearches = resumeData.researches.map((research, i) =>
      i === index ? { ...research, [name]: value } : research
    );
    setResumeData({ ...resumeData, researches: updatedResearches });
  };

  const addResearch = () => {
    setResumeData({
      ...resumeData,
      researches: [...resumeData.researches, { institution: '', role: '', location: '', startDate: '', endDate: '', description: '' }]
    });
  };

  const removeResearch = (index) => {
    const updatedResearches = resumeData.researches.filter((_, i) => i !== index);
    setResumeData({ ...resumeData, researches: updatedResearches });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Resume Data:", resumeData);
    // Further processing or saving of resume data can be done here
  };

  return (
    <div className="container">
      <h2>Create Resume</h2>
      <form onSubmit={handleSubmit}>
        {/* Personal Information Section */}
        <div className="row">
          <div className="form-group col-md-4">
            <label>Name</label>
            <input
              type="text"
              name="name"
              className="form-control"
              value={resumeData.name}
              onChange={handleChange}
            />
          </div>
          <div className="form-group col-md-4">
            <label>Contact</label>
            <input
              type="text"
              name="contact"
              className="form-control"
              value={resumeData.contact}
              onChange={handleChange}
            />
          </div>
          <div className="form-group col-md-4">
            <label>Personal Website</label>
            <input
              type="text"
              name="personalWebsite"
              className="form-control"
              value={resumeData.personalWebsite}
              onChange={handleChange}
            />
          </div>
        </div>

        {/* Current Education Section */}
        <h4>Current Education</h4>
        <div className="row">
          <div className="form-group col-md-2">
            <label>Degree</label>
            <input
              type="text"
              name="degree"
              className="form-control"
              value={resumeData.currentEducation.degree}
              onChange={(e) => handleEducationChange(null, e, 'current')}
            />
          </div>
          <div className="form-group col-md-2">
            <label>Program</label>
            <input
              type="text"
              name="program"
              className="form-control"
              value={resumeData.currentEducation.program}
              onChange={(e) => handleEducationChange(null, e, 'current')}
            />
          </div>
          <div className="form-group col-md-2">
            <label>Institution</label>
            <input
              type="text"
              name="institution"
              className="form-control"
              value={resumeData.currentEducation.institution}
              onChange={(e) => handleEducationChange(null, e, 'current')}
            />
          </div>
          <div className="form-group col-md-2">
            <label>Start Date</label>
            <input
              type="date"
              name="startDate"
              className="form-control"
              value={resumeData.currentEducation.startDate}
              onChange={(e) => handleEducationChange(null, e, 'current')}
            />
          </div>
          <div className="form-group col-md-2">
            <label>End Date</label>
            <input
              type="date"
              name="endDate"
              className="form-control"
              value={resumeData.currentEducation.endDate}
              onChange={(e) => handleEducationChange(null, e, 'current')}
            />
          </div>
        </div>

        {/* Additional Education History Section */}
        <h4>Additional Education</h4>
        {resumeData.additionalEducation.map((edu, index) => (
          <div className="row" key={index}>
            <div className="form-group col-md-2">
              <label>Degree</label>
              <input
                type="text"
                name="degree"
                className="form-control"
                value={edu.degree}
                onChange={(e) => handleEducationChange(index, e, 'additional')}
              />
            </div>
            <div className="form-group col-md-2">
              <label>Program</label>
              <input
                type="text"
                name="program"
                className="form-control"
                value={edu.program}
                onChange={(e) => handleEducationChange(index, e, 'additional')}
              />
            </div>
            <div className="form-group col-md-2">
              <label>Institution</label>
              <input
                type="text"
                name="institution"
                className="form-control"
                value={edu.institution}
                onChange={(e) => handleEducationChange(index, e, 'additional')}
              />
            </div>
            <div className="form-group col-md-2">
              <label>Start Date</label>
              <input
                type="date"
                name="startDate"
                className="form-control"
                value={edu.startDate}
                onChange={(e) => handleEducationChange(index, e, 'additional')}
              />
            </div>
            <div className="form-group col-md-2">
              <label>End Date</label>
              <input
                type="date"
                name="endDate"
                className="form-control"
                value={edu.endDate}
                onChange={(e) => handleEducationChange(index, e, 'additional')}
              />
            </div>
            <div className="form-group col-md-2 d-flex align-items-end">
              <button type="button" className="btn btn-danger" onClick={() => removeAdditionalEducation(index)}>
                -
              </button>
            </div>
          </div>
        ))}
        <button type="button" className="btn btn-secondary
        mt-3 mb-4" onClick={addAdditionalEducation}>
          + Add Additional Education
        </button>

        {/* Internship/Work Experience Section */}
        <h4>Internship/Work Experience</h4>
        {resumeData.internships.map((intern, index) => (
          <div key={index}>
            <div className="row">
              <div className="form-group col-md-2">
                <label>Company</label>
                <input
                  type="text"
                  name="company"
                  className="form-control"
                  value={intern.company}
                  onChange={(e) => handleInternshipChange(index, e)}
                />
              </div>
              <div className="form-group col-md-2">
                <label>Role</label>
                <input
                  type="text"
                  name="role"
                  className="form-control"
                  value={intern.role}
                  onChange={(e) => handleInternshipChange(index, e)}
                />
              </div>
              <div className="form-group col-md-2">
                <label>Location</label>
                <input
                  type="text"
                  name="location"
                  className="form-control"
                  value={intern.location}
                  onChange={(e) => handleInternshipChange(index, e)}
                />
              </div>
              <div className="form-group col-md-2">
                <label>Start Date</label>
                <input
                  type="date"
                  name="startDate"
                  className="form-control"
                  value={intern.startDate}
                  onChange={(e) => handleInternshipChange(index, e)}
                />
              </div>
              <div className="form-group col-md-2">
                <label>End Date</label>
                <input
                  type="date"
                  name="endDate"
                  className="form-control"
                  value={intern.endDate}
                  onChange={(e) => handleInternshipChange(index, e)}
                />
              </div>
              <div className="form-group col-md-2 d-flex align-items-end">
                <button type="button" className="btn btn-danger" onClick={() => removeInternship(index)}>
                  -
                </button>
              </div>
            </div>
            <div className="form-group">
              <label>Description</label>
              <textarea
                name="description"
                className="form-control"
                value={intern.description}
                onChange={(e) => handleInternshipChange(index, e)}
                rows="3"
              ></textarea>
            </div>
          </div>
        ))}
        <button type="button" className="btn btn-secondary mt-3 mb-4" onClick={addInternship}>
          + Add Internship/Work Experience
        </button>

        {/* Research Experience Section */}
        <h4>Research Experience</h4>
        {resumeData.researches.map((research, index) => (
          <div key={index}>
            <div className="row">
              <div className="form-group col-md-2">
                <label>Institution</label>
                <input
                  type="text"
                  name="institution"
                  className="form-control"
                  value={research.institution}
                  onChange={(e) => handleResearchChange(index, e)}
                />
              </div>
              <div className="form-group col-md-2">
                <label>Role</label>
                <input
                  type="text"
                  name="role"
                  className="form-control"
                  value={research.role}
                  onChange={(e) => handleResearchChange(index, e)}
                />
              </div>
              <div className="form-group col-md-2">
                <label>Location</label>
                <input
                  type="text"
                  name="location"
                  className="form-control"
                  value={research.location}
                  onChange={(e) => handleResearchChange(index, e)}
                />
              </div>
              <div className="form-group col-md-2">
                <label>Start Date</label>
                <input
                  type="date"
                  name="startDate"
                  className="form-control"
                  value={research.startDate}
                  onChange={(e) => handleResearchChange(index, e)}
                />
              </div>
              <div className="form-group col-md-2">
                <label>End Date</label>
                <input
                  type="date"
                  name="endDate"
                  className="form-control"
                  value={research.endDate}
                  onChange={(e) => handleResearchChange(index, e)}
                />
              </div>
              <div className="form-group col-md-2 d-flex align-items-end">
                <button type="button" className="btn btn-danger" onClick={() => removeResearch(index)}>
                  -
                </button>
              </div>
            </div>
            <div className="form-group">
              <label>Description</label>
              <textarea
                name="description"
                className="form-control"
                value={research.description}
                onChange={(e) => handleResearchChange(index, e)}
                rows="3"
              ></textarea>
            </div>
          </div>
        ))}
        <button type="button" className="btn btn-secondary mt-3 mb-4" onClick={addResearch}>
          + Add Research Experience
        </button>

        {/* Skills Section */}
        <h4>Skills</h4>
        <div className="form-group">
          <label>Skills</label>
          <textarea
            name="skills"
            className="form-control"
            value={resumeData.skills}
            onChange={handleChange}
            rows="4"
          ></textarea>
        </div>

        {/* Save Resume Button */}
        <div className="text-center mt-5">
          <button type="submit" className="btn btn-primary">Generate Resume</button>
        </div>
      </form>
    </div>
  );
}

export default ResumeCreation;
