import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function SchoolSelection() {
  const [formData, setFormData] = useState({
    degree: '',
    program: '',
    preferredRegion: '',
    currentEducation: {
      degree: '',
      program: '',
      institution: '',
      startDate: '',
      endDate: '',
      gpa: '', // Add GPA here
    },
    pastEducation: [],
    internships: '',
    researches: ''
  });

  const [suggestions, setSuggestions] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleLoadSavedInfo = () => {
    const savedInfo = JSON.parse(localStorage.getItem('userInfo'));
    if (savedInfo) {
      setFormData({
        ...formData,
        currentEducation: savedInfo.currentEducation || { degree: '', program: '', institution: '', startDate: '', endDate: '', gpa: '' },
        pastEducation: savedInfo.additionalEducation || [],
        internships: savedInfo.internships?.map((intern) => intern.description).join(', ') || '',
        researches: savedInfo.researches?.map((research) => research.description).join(', ') || ''
      });
    } else {
      alert('No saved user information found.');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleEducationChange = (index, e, type) => {
    const { name, value } = e.target;
    if (type === 'current') {
      setFormData({
        ...formData,
        currentEducation: { ...formData.currentEducation, [name]: value }
      });
    } else {
      const updatedEducation = formData.pastEducation.map((edu, i) =>
        i === index ? { ...edu, [name]: value } : edu
      );
      setFormData({ ...formData, pastEducation: updatedEducation });
    }
  };

  const addEducation = () => {
    setFormData({
      ...formData,
      pastEducation: [...formData.pastEducation, { degree: '', program: '', institution: '', startDate: '', endDate: '', gpa: '' }]
    });
  };

  const removeEducation = (index) => {
    const updatedEducation = formData.pastEducation.filter((_, i) => i !== index);
    setFormData({ ...formData, pastEducation: updatedEducation });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setSuggestions('');
    try {
      const response = await fetch('http://localhost:5001/school_selection', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      const data = await response.json();
      setSuggestions(data.school_suggestions);
    } catch (error) {
      console.error('Error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const loaderStyle = {
    border: '8px solid #f3f3f3',
    borderTop: '8px solid #3498db',
    borderRadius: '50%',
    width: '50px',
    height: '50px',
    animation: 'spin 2s linear infinite',
    margin: '20px auto',
  };

  const keyframesStyle = `
    @keyframes spin {
      0% { transform: rotate(0deg); }
      100% { transform: rotate(360deg); }
    }
  `;

  return (
    <div className="container">
      <style>{keyframesStyle}</style>
      <h2 className="mb-4 page-title">School Selection Plan</h2>
      <button type="button" className="btn btn-secondary mb-4" onClick={handleLoadSavedInfo}>
        Load Saved Information
      </button>
      <form onSubmit={handleSubmit}>
        <div className="row">
          <div className="form-group col-md-4">
            <label>Degree Applying For</label>
            <input
              type="text"
              name="degree"
              className="form-control"
              value={formData.degree}
              onChange={handleChange}
            />
          </div>
          <div className="form-group col-md-4">
            <label>Program Applying For</label>
            <input
              type="text"
              name="program"
              className="form-control"
              value={formData.program}
              onChange={handleChange}
            />
          </div>
          <div className="form-group col-md-4">
            <label>Preferred Region/Country</label>
            <input
              type="text"
              name="preferredRegion"
              className="form-control"
              value={formData.preferredRegion}
              onChange={handleChange}
            />
          </div>
        </div>

        {/* Current Education Section */}
        <div className="row">
          <div className="form-group col-md-2">
            <label>Current Degree</label>
            <input
              type="text"
              name="degree"
              className="form-control"
              value={formData.currentEducation.degree}
              onChange={(e) => handleEducationChange(null, e, 'current')}
            />
          </div>
          <div className="form-group col-md-2">
            <label>Current Program</label>
            <input
              type="text"
              name="program"
              className="form-control"
              value={formData.currentEducation.program}
              onChange={(e) => handleEducationChange(null, e, 'current')}
            />
          </div>
          <div className="form-group col-md-2">
            <label>Institution</label>
            <input
              type="text"
              name="institution"
              className="form-control"
              value={formData.currentEducation.institution}
              onChange={(e) => handleEducationChange(null, e, 'current')}
            />
          </div>
          <div className="form-group col-md-2">
            <label>Start Date</label>
            <input
              type="date"
              name="startDate"
              className="form-control w-100"
              value={formData.currentEducation.startDate}
              onChange={(e) => handleEducationChange(null, e, 'current')}
            />
          </div>
          <div className="form-group col-md-2">
            <label>End Date</label>
            <input
              type="date"
              name="endDate"
              className="form-control w-100"
              value={formData.currentEducation.endDate}
              onChange={(e) => handleEducationChange(null, e, 'current')}
            />
          </div>
          <div className="form-group col-md-2">
            <label>GPA</label>
            <input
              type="text"
              name="gpa"
              className="form-control"
              value={formData.currentEducation.gpa}
              onChange={(e) => handleEducationChange(null, e, 'current')}
            />
          </div>
        </div>

        {/* Past Education Section */}
        {formData.pastEducation.map((edu, index) => (
          <div className="row" key={index}>
            <div className="form-group col-md-2">
              <label>Degree</label>
              <input
                type="text"
                name="degree"
                className="form-control"
                value={edu.degree}
                onChange={(e) => handleEducationChange(index, e, 'past')}
              />
            </div>
            <div className="form-group col-md-2">
              <label>Program</label>
              <input
                type="text"
                name="program"
                className="form-control"
                value={edu.program}
                onChange={(e) => handleEducationChange(index, e, 'past')}
              />
            </div>
            <div className="form-group col-md-2">
              <label>Institution</label>
              <input
                type="text"
                name="institution"
                className="form-control"
                value={edu.institution}
                onChange={(e) => handleEducationChange(index, e, 'past')}
              />
            </div>
            <div className="form-group col-md-2">
              <label>Start Date</label>
              <input
                type="date"
                name="startDate"
                className="form-control w-100"
                value={edu.startDate}
                onChange={(e) => handleEducationChange(index, e, 'past')}
              />
            </div>
            <div className="form-group col-md-2">
              <label>End Date</label>
              <input
                type="date"
                name="endDate"
                className="form-control w-100"
                value={edu.endDate}
                onChange={(e) => handleEducationChange(index, e, 'past')}
              />
            </div>
            <div className="form-group col-md-2">
              <label>GPA</label>
              <input
                type="text"
                name="gpa"
                className="form-control"
                value={edu.gpa}
                onChange={(e) => handleEducationChange(index, e, 'past')}
              />
            </div>
            <div className="form-group col-md-1 d-flex align-items-end">
              <button type="button" className="btn btn-danger" onClick={() => removeEducation(index)}>
                -
              </button>
            </div>
          </div>
        ))}
        <button type="button" className="btn btn-secondary mt-3 mb-4" onClick={addEducation}>
          + Add Education History
        </button>

        {/* Internships and Research */}
        <div className="form-group">
          <label>Internship Experience</label>
          <textarea
            name="internships"
            className="form-control"
            value={formData.internships}
            onChange={handleChange}
            rows="4"
          ></textarea>
        </div>
        <div className="form-group">
          <label>Research Experience</label>
          <textarea
            name="researches"
            className="form-control"
            value={formData.researches}
            onChange={handleChange}
            rows="4"
          ></textarea>
        </div>

        <div className='text-center mt-5'>
          <button type="submit" className="btn btn-primary mt-3">Generate School Selection</button>
        </div>
      </form>
      {isLoading && <div style={loaderStyle}></div>}
      {suggestions && (
        <div className="mt-5">
          <h3>School Selection Suggestions:</h3>
          <pre className="border p-3">{suggestions}</pre>
        </div>
      )}
    </div>
  );
}

export default SchoolSelection;
