import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function SchoolSelectionPlan() {
  const [formData, setFormData] = useState({
    degree: '',
    program: '',
    preferredRegion: '',
    currentEducation: {
      degree: '',
      program: '',
      institution: '',
      startDate: '',
      endDate: '',
      gpa: '',
    },
    pastEducation: [],
    internships: '',
    researches: '',
  });

  const [isLoading, setIsLoading] = useState(false);

  // Load saved information from localStorage
  const handleLoadSavedInfo = () => {
    const savedInfo = JSON.parse(localStorage.getItem('userInfo'));
    if (savedInfo) {
      setFormData({
        ...formData,
        currentEducation: {
          degree: savedInfo.currentEducation?.degree || '',
          program: savedInfo.currentEducation?.program || '',
          institution: savedInfo.currentEducation?.institution || '',
          startDate: savedInfo.currentEducation?.startDate || '',
          endDate: savedInfo.currentEducation?.endDate || '',
          gpa: '', // Add GPA input for current education
        },
        pastEducation: savedInfo.additionalEducation.map(edu => ({
          degree: edu.degree || '',
          program: edu.program || '',
          institution: edu.institution || '',
          startDate: edu.startDate || '',
          endDate: edu.endDate || '',
          gpa: '',
        })),
        internships: savedInfo.internships?.[0]?.description || '',
        researches: savedInfo.researches?.[0]?.description || '',
      });
    } else {
      alert('No saved user information found.');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleEducationChange = (index, e, type) => {
    const { name, value } = e.target;
    if (type === 'current') {
      setFormData({
        ...formData,
        currentEducation: { ...formData.currentEducation, [name]: value },
      });
    } else {
      const updatedEducation = formData.pastEducation.map((edu, i) =>
        i === index ? { ...edu, [name]: value } : edu
      );
      setFormData({ ...formData, pastEducation: updatedEducation });
    }
  };

  const addPastEducation = () => {
    setFormData({
      ...formData,
      pastEducation: [
        ...formData.pastEducation,
        { degree: '', program: '', institution: '', startDate: '', endDate: '', gpa: '' },
      ],
    });
  };

  const removePastEducation = (index) => {
    const updatedEducation = formData.pastEducation.filter((_, i) => i !== index);
    setFormData({ ...formData, pastEducation: updatedEducation });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    console.log("Submitting data:", formData);
    try {
      // Add your backend call logic here for fetching school suggestions
      setIsLoading(false);
      alert('School suggestions generated successfully!');
    } catch (error) {
      console.error('Error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const loaderStyle = {
    border: '8px solid #f3f3f3',
    borderTop: '8px solid #3498db',
    borderRadius: '50%',
    width: '50px',
    height: '50px',
    animation: 'spin 2s linear infinite',
    margin: '20px auto',
  };

  const keyframesStyle = `
    @keyframes spin {
      0% { transform: rotate(0deg); }
      100% { transform: rotate(360deg); }
    }
  `;

  return (
    <div className="container">
      <style>{keyframesStyle}</style>
      <h2 className="mb-4 page-title">School Selection Plan</h2>
      <button type="button" className="btn btn-secondary mb-4" onClick={handleLoadSavedInfo}>
        Load Saved Information
      </button>
      <form onSubmit={handleSubmit}>
        {/* Applying For Section */}
        <div className="row">
          <div className="form-group col-md-4">
            <label>Degree Applying For</label>
            <input
              type="text"
              name="degree"
              className="form-control"
              value={formData.degree}
              onChange={handleChange}
            />
          </div>
          <div className="form-group col-md-4">
            <label>Program Applying For</label>
            <input
              type="text"
              name="program"
              className="form-control"
              value={formData.program}
              onChange={handleChange}
            />
          </div>
          <div className="form-group col-md-4">
            <label>Preferred Region/Country</label>
            <input
              type="text"
              name="preferredRegion"
              className="form-control"
              value={formData.preferredRegion}
              onChange={handleChange}
            />
          </div>
        </div>

        {/* Current Education Section */}
        <div className="row">
          <div className="form-group col-md-2">
            <label>Degree</label>
            <input
              type="text"
              name="degree"
              className="form-control"
              value={formData.currentEducation.degree}
              onChange={(e) => handleEducationChange(null, e, 'current')}
            />
          </div>
          <div className="form-group col-md-2">
            <label>Program</label>
            <input
              type="text"
              name="program"
              className="form-control"
              value={formData.currentEducation.program}
              onChange={(e) => handleEducationChange(null, e, 'current')}
            />
          </div>
          <div className="form-group col-md-2">
            <label>Institution</label>
            <input
              type="text"
              name="institution"
              className="form-control"
              value={formData.currentEducation.institution}
              onChange={(e) => handleEducationChange(null, e, 'current')}
            />
          </div>
          <div className="form-group col-md-2">
            <label>GPA</label>
            <input
              type="text"
              name="gpa"
              className="form-control"
              value={formData.currentEducation.gpa}
              onChange={(e) => handleEducationChange(null, e, 'current')}
            />
          </div>
          <div className="form-group col-md-2">
            <label>Start Date</label>
            <input
              type="date"
              name="startDate"
              className="form-control"
              value={formData.currentEducation.startDate}
              onChange={(e) => handleEducationChange(null, e, 'current')}
            />
          </div>
          <div className="form-group col-md-2">
            <label>End Date</label>
            <input
              type="date"
              name="endDate"
              className="form-control"
              value={formData.currentEducation.endDate}
              onChange={(e) => handleEducationChange(null, e, 'current')}
            />
          </div>
        </div>

        {/* Past Education Section */}
        {formData.pastEducation.map((edu, index) => (
          <div className="row" key={index}>
            <div className="form-group col-md-2">
              <label>Degree</label>
              <input
                type="text"
                name="degree"
                className="form-control"
                value={edu.degree}
                onChange={(e) => handleEducationChange(index, e, 'past')}
              />
            </div>
            <div className="form-group col-md-2">
              <label>Program</label>
              <input
                type="text"
                name="program"
                className="form-control"
                value={edu.program}
                onChange={(e) => handleEducationChange(index, e, 'past')}
              />
            </div>
            <div className="form-group col-md-2">
              <label>Institution</label>
              <input
                type="text"
                name="institution"
                className="form-control"
                value={edu.institution}
                onChange={(e) => handleEducationChange(index, e, 'past')}
              />
            </div>
            <div className="form-group col-md-2">
              <label>GPA</label>
              <input
                type="text"
                name="gpa"
                className="form-control"
                value={edu.gpa}
                onChange={(e) => handleEducationChange(index, e, 'past')}
              />
            </div>
            <div className="form-group col-md-2">
              <label>Start Date</label>
              <input
                type="date"
                name="startDate"
                className="form-control"
                value={edu.startDate}
                onChange={(e) => handleEducationChange(index, e, 'past')}
              />
            </div>
            <div className="form-group col-md-2">
              <label>End Date</label>
              <input
                type="date"
                name="endDate"
                className="form-control"
                value={edu.endDate}
                onChange={(e) => handleEducationChange(index, e, 'past')}
              />
            </div>
            <div className="form-group col-md-1 d-flex align-items-end">
              <button type="button" className="btn btn-danger" onClick={() => removePastEducation(index)}>
                -
              </button>
            </div>
          </div>
        ))}
        <button type="button" className="btn btn-secondary mt-3 mb-3" onClick={addPastEducation}>
          + Add Past Education
        </button>

        {/* Internship and Research Experience */}
        <div className="form-group">
          <label>Internship Experience</label>
          <textarea
            name="internships"
            className="form-control"
            value={formData.internships}
            onChange={handleChange}
            rows="4"
          ></textarea>
        </div>
        <div className="form-group">
          <label>Research Experience</label>
          <textarea
            name="researches"
            className="form-control"
            value={formData.researches}
            onChange={handleChange}
            rows="4"
          ></textarea>
        </div>

        <div className='text-center mt-5'>
          <button type="submit" className="btn btn-primary mt-3">Generate School Suggestions</button>
        </div>
      </form>
      {isLoading && <div style={loaderStyle}></div>}
    </div>
  );
}

export default SchoolSelectionPlan;
