import React, { useState } from 'react';

function SchoolSelectionPlan() {
  const [formData, setFormData] = useState({
    gpa: '',
    major: '',
    country: '',
    preferences: '',
  });
  const [schoolPlan, setSchoolPlan] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/school_selection', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      const data = await response.json();
      setSchoolPlan(data.school_plan);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <h2>School Selection Plan</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>GPA</label>
          <input type="text" name="gpa" className="form-control" value={formData.gpa} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Major</label>
          <input type="text" name="major" className="form-control" value={formData.major} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Preferred Country</label>
          <input type="text" name="country" className="form-control" value={formData.country} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Preferences (e.g., location, climate, campus size)</label>
          <textarea name="preferences" className="form-control" value={formData.preferences} onChange={handleChange} rows="4"></textarea>
        </div>
        <button type="submit" className="btn btn-primary">Get School Selection Plan</button>
      </form>
      {schoolPlan && (
        <div className="mt-5">
          <h3>Suggested School Plan:</h3>
          <p className="border p-3">{schoolPlan}</p>
        </div>
      )}
    </div>
  );
}

export default SchoolSelectionPlan;
