import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function UserInformation() {
  const [currentEducation, setCurrentEducation] = useState({
    degree: '',
    program: '',
    institution: ''
  });

  const [additionalEducation, setAdditionalEducation] = useState([]);
  const [internships, setInternships] = useState([]);
  const [researches, setResearches] = useState([]);

  const handleCurrentEducationChange = (e) => {
    const { name, value } = e.target;
    setCurrentEducation({ ...currentEducation, [name]: value });
  };

  const handleAdditionalEducationChange = (index, e) => {
    const { name, value } = e.target;
    const updatedEducation = additionalEducation.map((edu, i) =>
      i === index ? { ...edu, [name]: value } : edu
    );
    setAdditionalEducation(updatedEducation);
  };

  const addAdditionalEducation = () => {
    setAdditionalEducation([...additionalEducation, { degree: '', program: '', institution: '' }]);
  };

  const removeAdditionalEducation = (index) => {
    const updatedEducation = additionalEducation.filter((_, i) => i !== index);
    setAdditionalEducation(updatedEducation);
  };

  const handleInternshipChange = (index, e) => {
    const { name, value } = e.target;
    const updatedInternships = internships.map((intern, i) =>
      i === index ? { ...intern, [name]: value } : intern
    );
    setInternships(updatedInternships);
  };

  const addInternship = () => {
    setInternships([...internships, { company: '', role: '', location: '', startDate: '', endDate: '', description: '' }]);
  };

  const removeInternship = (index) => {
    const updatedInternships = internships.filter((_, i) => i !== index);
    setInternships(updatedInternships);
  };

  const handleResearchChange = (index, e) => {
    const { name, value } = e.target;
    const updatedResearches = researches.map((research, i) =>
      i === index ? { ...research, [name]: value } : research
    );
    setResearches(updatedResearches);
  };

  const addResearch = () => {
    setResearches([...researches, { institution: '', role: '', location: '', startDate: '', endDate: '', description: '' }]);
  };

  const removeResearch = (index) => {
    const updatedResearches = researches.filter((_, i) => i !== index);
    setResearches(updatedResearches);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const userInfo = {
      education: [currentEducation, ...additionalEducation],
      internships,
      researches
    };
    localStorage.setItem('userInfo', JSON.stringify(userInfo));
    alert('User information saved!');
  };

  const handleLoadStoredInfo = () => {
    const savedInfo = JSON.parse(localStorage.getItem('userInfo'));
    if (savedInfo) {
      setCurrentEducation(savedInfo.education[0] || { degree: '', program: '', institution: '' });
      setAdditionalEducation(savedInfo.education.slice(1) || []);
      setInternships(savedInfo.internships || []);
      setResearches(savedInfo.researches || []);
    } else {
      alert('No saved user information found.');
    }
  };

  return (
    <div className="container">
      <h2>User Information</h2>
      <button type="button" className="btn btn-secondary mb-4" onClick={handleLoadStoredInfo}>
        Load Saved Information
      </button>
      <form onSubmit={handleSubmit}>
        {/* Current Education Section */}
        <h4>Current Education</h4>
        <div className="row">
          <div className="form-group col-md-4">
            <label>Degree</label>
            <input
              type="text"
              name="degree"
              className="form-control"
              value={currentEducation.degree}
              onChange={handleCurrentEducationChange}
            />
          </div>
          <div className="form-group col-md-4">
            <label>Program</label>
            <input
              type="text"
              name="program"
              className="form-control"
              value={currentEducation.program}
              onChange={handleCurrentEducationChange}
            />
          </div>
          <div className="form-group col-md-4">
            <label>Institution</label>
            <input
              type="text"
              name="institution"
              className="form-control"
              value={currentEducation.institution}
              onChange={handleCurrentEducationChange}
            />
          </div>
        </div>

        {/* Additional Education History Section */}
        <h4>Additional Education</h4>
        {additionalEducation.map((edu, index) => (
          <div className="row" key={index}>
            <div className="form-group col-md-4">
              <label>Degree</label>
              <input
                type="text"
                name="degree"
                className="form-control"
                value={edu.degree}
                onChange={(e) => handleAdditionalEducationChange(index, e)}
              />
            </div>
            <div className="form-group col-md-4">
              <label>Program</label>
              <input
                type="text"
                name="program"
                className="form-control"
                value={edu.program}
                onChange={(e) => handleAdditionalEducationChange(index, e)}
              />
            </div>
            <div className="form-group col-md-3">
              <label>Institution</label>
              <input
                type="text"
                name="institution"
                className="form-control"
                value={edu.institution}
                onChange={(e) => handleAdditionalEducationChange(index, e)}
              />
            </div>
            <div className="form-group col-md-1 d-flex align-items-end">
              <button type="button" className="btn btn-danger" onClick={() => removeAdditionalEducation(index)}>
                -
              </button>
            </div>
          </div>
        ))}
        <button type="button" className="btn btn-secondary mt-3 mb-4" onClick={addAdditionalEducation}>
          + Add Additional Education
        </button>

        {/* Internship/Work Experience Section */}
        <h4>Internship/Work Experience</h4>
        {internships.map((intern, index) => (
          <div key={index}>
            <div className="row">
              <div className="form-group col-md-2">
                <label>Company</label>
                <input
                  type="text"
                  name="company"
                  className="form-control"
                  value={intern.company}
                  onChange={(e) => handleInternshipChange(index, e)}
                />
              </div>
              <div className="form-group col-md-2">
                <label>Role</label>
                <input
                  type="text"
                  name="role"
                  className="form-control"
                  value={intern.role}
                  onChange={(e) => handleInternshipChange(index, e)}
                />
              </div>
              <div className="form-group col-md-2">
                <label>Location</label>
                <input
                  type="text"
                  name="location"
                  className="form-control"
                  value={intern.location}
                  onChange={(e) => handleInternshipChange(index, e)}
                />
              </div>
              <div className="form-group col-md-2">
                <label>Start Date</label>
                <input
                  type="date"
                  name="startDate"
                  className="form-control"
                  value={intern.startDate}
                  onChange={(e) => handleInternshipChange(index, e)}
                />
              </div>
              <div className="form-group col-md-2">
                <label>End Date</label>
                <input
                  type="date"
                  name="endDate"
                  className="form-control"
                  value={intern.endDate}
                  onChange={(e) => handleInternshipChange(index, e)}
                />
              </div>
              <div className="form-group col-md-2 d-flex align-items-end">
                <button type="button" className="btn btn-danger" onClick={() => removeInternship(index)}>
                  -
                </button>
              </div>
            </div>
            <div className="form-group">
              <label>Description</label>
              <textarea
                name="description"
                className="form-control"
                value={intern.description}
                onChange={(e) => handleInternshipChange(index, e)}
                rows="3"
              ></textarea>
            </div>
          </div>
        ))}
        <button type="button" className="btn btn-secondary mt-3 mb-4" onClick={addInternship}>
          + Add Internship/Work Experience
        </button>

        {/* Research Experience Section */}
        <h4>Research Experience</h4>
        {researches.map((research, index) => (
          <div key={index}>
            <div className="row">
              <div className="form-group col-md-2">
                <label>Institution</label>
                <input
                  type="text"
                  name="institution"
                  className="form-control"
                  value={research.institution}
                  onChange={(e) => handleResearchChange(index, e)}
                />
              </div>
              <div className="form-group col-md-2">
                <label>Role</label>
                <input
                  type="text"
                  name="role"
                  className="form-control"
                  value={research.role}
                  onChange={(e) => handleResearchChange(index, e)}
                />
              </div>
              <div className="form-group col-md-2">
                <label>Location</label>
                <input
                  type="text"
                  name="location"
                  className="form-control"
                  value={research.location}
                  onChange={(e) => handleResearchChange(index, e)}
                />
              </div>
              <div className="form-group col-md-2">
                <label>Start Date</label>
                <input
                  type="date"
                  name="startDate"
                  className="form-control"
                  value={research.startDate}
                  onChange={(e) => handleResearchChange(index, e)}
                />
              </div>
              <div className="form-group col-md-2">
                <label>End Date</label>
                <input
                  type="date"
                  name="endDate"
                  className="form-control"
                  value={research.endDate}
                  onChange={(e) => handleResearchChange(index, e)}
                />
              </div>
              <div className="form-group col-md-2 d-flex align-items-end">
                <button type="button" className="btn btn-danger" onClick={() => removeResearch(index)}>
                  -
                </button>
              </div>
            </div>
            <div className="form-group">
              <label>Description</label>
              <textarea
                name="description"
                className="form-control"
                value={research.description}
                onChange={(e) => handleResearchChange(index, e)}
                rows="3"
              ></textarea>
            </div>
          </div>
        ))}
        <button type="button" className="btn btn-secondary mt-3 mb-4" onClick={addResearch}>
          + Add Research Experience
        </button>

        {/* Save Information Button */}
        <div className="text-center mt-5">
          <button type="submit" className="btn btn-primary">Save Information</button>
        </div>
      </form>
    </div>
  );
}

export default UserInformation;
