import React, { useState } from 'react';

function ResumePolishing() {
  const [formData, setFormData] = useState({ resume: '' });
  const [polishedResume, setPolishedResume] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/resume_polish', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      const data = await response.json();
      setPolishedResume(data.polished_resume);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <h2>Polish Resume</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Paste Your Resume Here</label>
          <textarea name="resume" className="form-control" value={formData.resume} onChange={handleChange} rows="10"></textarea>
        </div>
        <button type="submit" className="btn btn-primary">Polish My Resume</button>
      </form>
      {polishedResume && (
        <div className="mt-5">
          <h3>Polished Resume:</h3>
          <p className="border p-3">{polishedResume}</p>
        </div>
      )}
    </div>
  );
}

export default ResumePolishing;
