import React, { useState } from 'react';

function PersonalStatementCreation() {
  const [formData, setFormData] = useState({
    current_degree: '',
    current_program: '',
    institution: '',
    degree: '',
    program: '',
    institution_applying: '',
    internship: '',
    research: '',
    other_requirements: '',
    word_count: '',
    additional_education: [{ degree: '', program: '', institution: '' }]
  });

  const [generatedPS, setGeneratedPS] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleEducationChange = (index, e) => {
    const { name, value } = e.target;
    const newEducation = formData.additional_education.map((edu, eduIndex) =>
      eduIndex === index ? { ...edu, [name]: value } : edu
    );
    setFormData({ ...formData, additional_education: newEducation });
  };

  const addEducation = () => {
    setFormData({
      ...formData,
      additional_education: [...formData.additional_education, { degree: '', program: '', institution: '' }]
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/ps_create', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      const data = await response.json();
      setGeneratedPS(data.personal_statement);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <h2>Create Your Personal Statement</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Current Degree: </label>
          <input type="text" name="current_degree" value={formData.current_degree} onChange={handleChange} />
        </div>
        <div>
          <label>Current Program: </label>
          <input type="text" name="current_program" value={formData.current_program} onChange={handleChange} />
        </div>
        <div>
          <label>Institution: </label>
          <input type="text" name="institution" value={formData.institution} onChange={handleChange} />
        </div>
        <div>
          <label>Degree Applying For: </label>
          <input type="text" name="degree" value={formData.degree} onChange={handleChange} />
        </div>
        <div>
          <label>Program Applying For: </label>
          <input type="text" name="program" value={formData.program} onChange={handleChange} />
        </div>
        <div>
          <label>Institution Applying To: </label>
          <input type="text" name="institution_applying" value={formData.institution_applying} onChange={handleChange} />
        </div>
        <div>
          <label>Internship Experience: </label>
          <input type="text" name="internship" value={formData.internship} onChange={handleChange} />
        </div>
        <div>
          <label>Research Experience: </label>
          <input type="text" name="research" value={formData.research} onChange={handleChange} />
        </div>
        <div>
          <label>Other Requirements: </label>
          <input type="text" name="other_requirements" value={formData.other_requirements} onChange={handleChange} />
        </div>
        <div>
          <label>Word Count: </label>
          <input type="text" name="word_count" value={formData.word_count} onChange={handleChange} />
        </div>
        {formData.additional_education.map((edu, index) => (
          <div key={index}>
            <label>Additional Degree {index + 1}: </label>
            <input
              type="text"
              name="degree"
              value={edu.degree}
              onChange={(e) => handleEducationChange(index, e)}
            />
            <label>Program: </label>
            <input
              type="text"
              name="program"
              value={edu.program}
              onChange={(e) => handleEducationChange(index, e)}
            />
            <label>Institution: </label>
            <input
              type="text"
              name="institution"
              value={edu.institution}
              onChange={(e) => handleEducationChange(index, e)}
            />
          </div>
        ))}
        <button type="button" onClick={addEducation}>Add More Education</button>
        <button type="submit">Generate Personal Statement</button>
      </form>
      {generatedPS && (
        <div>
          <h3>Generated Personal Statement:</h3>
          <p>{generatedPS}</p>
        </div>
      )}
    </div>
  );
}

export default PersonalStatementCreation;
