import React, { useState } from 'react';

function ResumePolishing() {
  const [file, setFile] = useState(null);
  const [polishedResume, setPolishedResume] = useState('');

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!file) {
      alert('Please upload a PDF file.');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch('http://localhost:5000/resume_polish', {
        method: 'POST',
        body: formData
      });
      const data = await response.json();
      setPolishedResume(data.polished_resume);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="container mt-5">
      {/* Add margin to the top to avoid overlapping with the navbar */}
      <h2 className="mb-4">Polish Resume</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Upload Your Resume (PDF only)</label>
          <input
            type="file"
            className="form-control mb-3"
            onChange={handleFileChange}
            accept=".pdf"
          />
        </div>
        <button type="submit" className="btn btn-primary mb-4">Polish My Resume</button>
      </form>
      {polishedResume && (
        <div className="mt-5">
          <h3>Polished Resume:</h3>
          <p className="border p-3">{polishedResume}</p>
        </div>
      )}
    </div>
  );
}

export default ResumePolishing;
