import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function SchoolSelectionPlan() {
  const [formData, setFormData] = useState({
    desiredProgram: '',
    targetRegion: '',
    educationRecords: [],
    internships: [],
    researches: [],
  });

  const [isLoading, setIsLoading] = useState(false);

  // Load saved information from localStorage when the user clicks the load button
  const handleLoadSavedInfo = () => {
    const savedInfo = JSON.parse(localStorage.getItem('userInfo'));
    if (savedInfo) {
      setFormData({
        ...formData,
        educationRecords: savedInfo.additionalEducation.map((edu) => ({
          degree: edu.degree || '',
          program: edu.program || '',
          institution: edu.institution || '',
          startDate: edu.startDate || '',
          endDate: edu.endDate || '',
          gpa: edu.gpa || '', // Keeping GPA field
        })),
        internships: savedInfo.internships || [],
        researches: savedInfo.researches || [],
      });
    } else {
      alert('No saved user information found.');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleEducationChange = (index, e) => {
    const { name, value } = e.target;
    const updatedEducation = formData.educationRecords.map((edu, i) =>
      i === index ? { ...edu, [name]: value } : edu
    );
    setFormData({ ...formData, educationRecords: updatedEducation });
  };

  const addEducationRecord = () => {
    setFormData({
      ...formData,
      educationRecords: [
        ...formData.educationRecords,
        { degree: '', program: '', institution: '', startDate: '', endDate: '', gpa: '' },
      ],
    });
  };

  const removeEducationRecord = (index) => {
    const updatedEducation = formData.educationRecords.filter((_, i) => i !== index);
    setFormData({ ...formData, educationRecords: updatedEducation });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    try {
      // Mock backend call to fetch school recommendations
      // Replace with your logic to fetch data based on user background
      setTimeout(() => {
        alert('Suggested Schools have been updated!');
        setIsLoading(false);
      }, 2000);
    } catch (error) {
      console.error('Error:', error);
      setIsLoading(false);
    }
  };

  const loaderStyle = {
    border: '8px solid #f3f3f3',
    borderTop: '8px solid #3498db',
    borderRadius: '50%',
    width: '50px',
    height: '50px',
    animation: 'spin 2s linear infinite',
    margin: '20px auto',
  };

  const keyframesStyle = `
    @keyframes spin {
      0% { transform: rotate(0deg); }
      100% { transform: rotate(360deg); }
    }
  `;

  return (
    <div className="container mt-5">
      <style>{keyframesStyle}</style> {/* Add the keyframes to the document */}
      <h2 className="mb-4 page-title">School Selection Plan</h2>
      <button type="button" className="btn btn-secondary mb-4" onClick={handleLoadSavedInfo}>
        Load Saved Information
      </button>
      <form onSubmit={handleSubmit}>
        <div className="row">
          <div className="form-group col-md-6">
            <label>Desired Program</label>
            <input
              type="text"
              name="desiredProgram"
              className="form-control"
              value={formData.desiredProgram}
              onChange={handleChange}
            />
          </div>
          <div className="form-group col-md-6">
            <label>Target Region/Country</label>
            <input
              type="text"
              name="targetRegion"
              className="form-control"
              value={formData.targetRegion}
              onChange={handleChange}
            />
          </div>
        </div>

        {/* Education Records */}
        <h4>Education Records</h4>
        {formData.educationRecords.map((edu, index) => (
          <div className="row" key={index}>
            <div className="form-group col-md-2">
              <label>Degree</label>
              <input
                type="text"
                name="degree"
                className="form-control"
                value={edu.degree}
                onChange={(e) => handleEducationChange(index, e)}
              />
            </div>
            <div className="form-group col-md-2">
              <label>Program</label>
              <input
                type="text"
                name="program"
                className="form-control"
                value={edu.program}
                onChange={(e) => handleEducationChange(index, e)}
              />
            </div>
            <div className="form-group col-md-2">
              <label>Institution</label>
              <input
                type="text"
                name="institution"
                className="form-control"
                value={edu.institution}
                onChange={(e) => handleEducationChange(index, e)}
              />
            </div>
            <div className="form-group col-md-2">
              <label>GPA</label>
              <input
                type="text"
                name="gpa"
                className="form-control"
                value={edu.gpa}
                onChange={(e) => handleEducationChange(index, e)}
              />
            </div>
            <div className="form-group col-md-2">
              <label>Start Date</label>
              <input
                type="date"
                name="startDate"
                className="form-control"
                value={edu.startDate}
                onChange={(e) => handleEducationChange(index, e)}
              />
            </div>
            <div className="form-group col-md-2">
              <label>End Date</label>
              <input
                type="date"
                name="endDate"
                className="form-control"
                value={edu.endDate}
                onChange={(e) => handleEducationChange(index, e)}
              />
            </div>
            <div className="form-group col-md-1 d-flex align-items-end">
              <button type="button" className="btn btn-danger" onClick={() => removeEducationRecord(index)}>
                -
              </button>
            </div>
          </div>
        ))}
        <button type="button" className="btn btn-secondary mt-3 mb-4" onClick={addEducationRecord}>
          + Add Education Record
        </button>

        <div className="text-center">
          <button type="submit" className="btn btn-primary mt-3">Generate School Suggestions</button>
        </div>
      </form>

      {/* Loading animation */}
      {isLoading && <div style={loaderStyle}></div>}
    </div>
  );
}

export default SchoolSelectionPlan;
