import React, { useState } from 'react';

function ResumeCreation() {
  const [formData, setFormData] = useState({
    name: '',
    contact: '',
    education: '',
    experience: '',
    skills: '',
  });
  const [generatedResume, setGeneratedResume] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/resume_create', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      const data = await response.json();
      setGeneratedResume(data.generated_resume);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <h2>Create Resume</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Name</label>
          <input type="text" name="name" className="form-control" value={formData.name} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Contact Information</label>
          <input type="text" name="contact" className="form-control" value={formData.contact} onChange={handleChange} />
        </div>
        <div className="form-group">
          <label>Education</label>
          <textarea name="education" className="form-control" value={formData.education} onChange={handleChange} rows="4"></textarea>
        </div>
        <div className="form-group">
          <label>Experience</label>
          <textarea name="experience" className="form-control" value={formData.experience} onChange={handleChange} rows="4"></textarea>
        </div>
        <div className="form-group">
          <label>Skills</label>
          <textarea name="skills" className="form-control" value={formData.skills} onChange={handleChange} rows="3"></textarea>
        </div>
        <button type="submit" className="btn btn-primary">Generate Resume</button>
      </form>
      {generatedResume && (
        <div className="mt-5">
          <h3>Generated Resume:</h3>
          <p className="border p-3">{generatedResume}</p>
        </div>
      )}
    </div>
  );
}

export default ResumeCreation;
